import React from 'react';
import './Track.css';


class Track extends React.Component {


	constructor(props) {
		super(props);
		this.state = {
			isRemoval: false
		}
		this.renderAction = this.renderAction.bind(this);
		this.addTrack = this.addTrack.bind(this);
		this.removeTrack = this.removeTrack.bind(this);
	};

//conditionally render something - set the state and create a method that renders something different according to the state.
//Then call the method in the element of the page
	renderAction() {
		if(this.state.isRemoval) {
			return <a className="Track-action" onClick={this.addTrack}>+</a>
		}
		else {
			return <a className="Track-action" onClick={this.removeTrack}>-</a>
		}
	}

	addTrack(){
		this.props.onAdd(this.props.track);
		this.setState({isRemoval: false})
	}

	removeTrack(){
		this.props.onRemove(this.props.track);
	}

	render() {
		return(
			<div className="Track">
  			  <div className="Track-information">
    		<h3>{this.props.track.name}</h3>
    <p>{this.props.track.artist} | {this.props.track.album}</p>
  </div>
  {this.renderAction()}
</div>
			);
	}
}

export default Track;
